SELECT BUY_NO, MEM_ID, PAYMENT, TO_CHAR(PAY_TIME, 'YYYY-MM-DD HH24:MI:SS') AS PAY_TIME , NAME, PHONE, ADDR || ' ' || ADDR_D AS ADDR, ADDR_NO
